mapper.select:
    select cert_id, cert_name, cert_date, cert_inst, id, verify, tag
	from Certificate
	where id = #{id}

select cert_name
from Certificate
where tag = #{tag}

mapper.update:
update Certificate
set cert_name=#{cert_name}, cert_date=#{cert_date}, cert_inst=#{cert_inst}, id=#{id}, verify=#{verify}, tag=#{tag}
where cert_id=#{cert_id}
