mapper.update:
update UserAdditional
set   nickname=#{nickname}, pic=#{pic}, rank=#{rank}, score=#{score}, description=#{description},
		zipcode=#{zipcode}, address1=#{address1}, address2=#{address2}, add_latitude=#{add_latitude}, 
		add_longitude=#{add_longitude}, introduce=#{introduce}, grade=#{grade}
where id=#{id}
