mapper.select:
    select id, nickname, pic, rank, score, description, zipcode, address1, address2
	from UserAdditional 
	where id = #{id}

mapper.update:
update UserAdditional
set   nickname=#{nickname}, pic=#{pic}, rank=#{rank}, score=#{score}, description=#{description},
		zipcode=#{zipcode}, address1=#{address1}, address2=#{address2}
where id=#{id}
