mapper.select:
    select id, password, name, phone, birth, gender, perm1
	from UserEssential 
	where id = #{id}

    select id, password, name, phone, birth, gender, perm1
	from UserEssential 
	where id = #{id} and password = #{password}

mapper.update:
update UserEssential
set password=#{password}, name=#{name}, phone=#{phone}, birth=#{birth}, gender=#{gender}, perm1=#{perm1}}
where	id=#{id}
