mapper.select:
    select id, password, name, nickname, phone
	from UserEssential 
	where id = #{id}

    select id, password, name, nickname, phone
	from UserEssential 
	where id = #{id} and password = #{password}

mapper.update:
update UserEssential
set password=#{password}, name=#{name}, nickname=#{nickname}, phone=#{phone}}
where	id=#{id}

mapper.select:
    select COUNT(*)
	from UserEssential 
	where id = #{id}
