mapper.select:
    select id, password, name, nickname, phone, birth, pic, address1, address2, type 
	from Account 
	where id = #{id}

mapper.update:
update Account
set password=#{password}, name=#{name}, nickname=#{nickname}, phone=#{phone}, birth=#{birth}, 
pic=#{pic}, address1=#{address1}, address2=#{address2}, type=#{type}
where
id=#{id}
